import {useState} from 'react';

const Tasks = () => {
    const [tasks, setTasks] = useState(
        [
            {
                id: 1,
                text:'do presentation',
                day: 'sunday',
                reminder: false
            },
            {
                id: 2,
                text:'do live coding',
                day: 'tuesday',
                reminder: false
            },
            {
                id: 3,
                text:'attend meeting',
                day: 'wednesday',
                reminder: false
            }
        ]
    )
  return (
    <>
      {tasks.map((task) =>
        <h3 key={task.id}>{task.text}</h3>
      )}
    </>
  )
}

export default Tasks
