import Header from './components/Header';
import PropTypes from 'prop-types';
import Tasks from './components/Tasks';
import AddForm from './components/AddForm';
import {useState, useEffect} from 'react';

function App(){
    const[toggleAdd, setToggleAdd] = useState(false)
    const [tasks, setTasks] = useState([])
    useEffect(() => {
        const getTasks = async () => {
            const tasksFromServer = await fetchTasks()
            setTasks(tasksFromServer)
        }
        getTasks()
    }, [])
    const fetchTasks = async () => {
        const res = await fetch('http://localhost:8080/tasks/all')
        const data = await res.json()
        console.log(data)
        return data
    }
    const deleteTask = async (id) => {
        await fetch(`http://localhost:8080/tasks${id}`
                    ,{method: 'DELETE'})
        setTasks(tasks.filter((task) => task.id !== id))
    }
    const toggleReminder = async (id) => {
        const toggled = await fetch(`http://localhost:8080/tasks${id}`
        ,{method: 'PUT'})
        setTasks(
            tasks.map((task) => task.id === id ? {...task, reminder: !toggled.reminder}:toggled)
            )
    }
    const saveTask = (task) => {
        const save = () => {
            const saveTasks = async () => {
                const res = await fetch('http://localhost/tasks',{})
            }
        }
        setTasks([...tasks, task])
    }
    return(
        <div className='container'>
            <Header title='Task Tracker' toggleAdd = {() => setToggleAdd(!toggleAdd)} showAdd={toggleAdd}/>
            {toggleAdd && <AddForm saveTask={saveTask} />}
            {tasks.length > 0 ? <Tasks tasks={tasks} onDelete={deleteTask} onToggle={toggleReminder}/>: <h2>No Tasks To Show</h2>}
        </div>
    )
}
Header.defaultProps = {
    title: 'Task Tracker'
}
Header.propTypes = {
    title: PropTypes.string.isRequired,
    tasks: PropTypes.array
}

export default App;
